sect2.programlisting:
select current_timestamp from MyEvent
// equivalent to
select current_timestamp() from MyEvent

select prev(1, symbol) from Trade
// ... equivalent to ...
select prev(symbol) from Trade
// ... and ...
select prev(symbol, 1) from Trade

select prevtail(1, symbol) from Trade
// ... equivalent to ...
select prevtail(symbol) from Trade
// ... and ...
select prevtail(symbol, 1) from Trade

sect4.programlisting:
public class CountMinSketchAgentStringUTF16 implements CountMinSketchAgent {
  public Class[] getAcceptableValueTypes() {
    return new Class[] {String.class};
  }

  public void add(CountMinSketchAgentContextAdd ctx) {
    String text = (String) ctx.getValue();
    if (text == null) {
      return;
    }
    byte[] bytes = toBytesUTF16(text);
    ctx.getState().add(bytes, 1);  // increase count by 1
  }

  public Long estimate(CountMinSketchAgentContextEstimate ctx) {
    String text = (String) ctx.getValue();
    if (text == null) {
      return null;
    }
    byte[] bytes = toBytesUTF16(text);
    return ctx.getState().frequency(bytes);
  }
}

entry.programlisting:
select symbol, sum(price) 
from StockTickEvent#time(30 sec) 
having leaving()

select colour, rate(timestamp) as rate 
from StockTickEvent#groupwin(symbol)#length(4) 
group by symbol
